import React, { useCallback, useMemo, useState, useEffect } from "react"
// import {RxCross1} from "@react-icons/all-files/rx/RxCross1";
// import { BsCircle } from "@react-icons/all-files/bs/BsCircle";

import { SquareFilling, SquareState } from "../../types/square";
import { determineEndOfGame } from "../../helpers/determineEndOfGame";

import './GameField.css'

export const GameField = () => {
    const [squares, setSquaresState] = useState<SquareState[]>([
        null,
        null,
        null,
        null,
        null,
        null,
        null,
        null,
        null,
    ]);

    const [isGameOver, setGameState] = useState<boolean>(false)
    const [winnerZeroOrCross, setWinner] = useState<SquareState | undefined>(undefined);

    useEffect(() => {
      const { isEnd, winner } = determineEndOfGame(squares);
      setGameState(isEnd);
      setWinner(winner);
    }, [squares, setGameState, setWinner]);

    const findAndFillEmptySquare = useCallback(() => {
        const idx = squares.findLastIndex((item) => item === null);
        const newState = squares.slice();
        newState.splice(idx, 1, SquareFilling.Zero);
        setSquaresState(newState);
    }, [squares, setSquaresState]);

    const onSquareClick = useCallback(
      (state: SquareState, idx: number) => {
        if (!state) {
            const newState = squares.slice();
            newState.splice(idx, 1, SquareFilling.Cross);
            console.log("newState", newState);
            setSquaresState(newState);
            //findAndFillEmptySquare();
        }
      },
      [setSquaresState, findAndFillEmptySquare]
    );

    const field = useMemo(() => {
        return squares.map((item, idx) => {
            return (
                <Square
                key={idx}
                state={item}
                onClick={() => {
                    onSquareClick(item, idx);
                }}
                />
            );
        });
    }, [squares, setSquaresState, onSquareClick]);


    return <div>
        <div className="game-field">{field}</div>
        {isGameOver && <span>Выиграли  {winnerZeroOrCross}</span>}
    </div>;
}


export const Square = ({state, onClick}: {state: SquareState, onClick: () => void}) => {

    return (
      <div className={"game-field__square"} onClick={onClick}>
        hey
        {state === SquareFilling.Cross && (
          <div className={"game-field__square-content"}>крестик</div>
        )}
        {state === SquareFilling.Zero && <div className={"game-field__square-content"}>нолик</div>}
      </div>
    );
}

export const mapFiilingToWord = new Map<SquareState, string>([[SquareState,]]);
