import React, { useCallback, useMemo, useState } from "react"
// import {RxCross1} from "@react-icons/all-files/rx/RxCross1";
// import { BsCircle } from "@react-icons/all-files/bs/BsCircle";

import { SquareFilling, SquareState } from "../../types/square";

import './GameField.css'

export const GameField = () => {
    const [squares, setSquaresState] = useState<SquareState[]>([
        null,
        null,
        null,
        null,
        null,
        null,
        null,
        null,
        null,
    ]);

    const findAndFillEmptySquare = useCallback(() => {
        const idx = squares.findLastIndex((item) => item === null);
        const newState = squares.slice();
        newState.splice(idx, 1, SquareFilling.Zero);
        setSquaresState(newState);
    }, [squares, setSquaresState]);

    const onSquareClick = useCallback((state: SquareState, onUpdate: (state: SquareState) => void) => {
        if (state === null) {
            onUpdate(SquareFilling.Cross);
            findAndFillEmptySquare();
        } 
    }, [findAndFillEmptySquare])

    const field = useMemo(() => {
        return squares.map((item, idx) => {
            const onUpdate = (state: SquareState) => {
                const newState = squares.slice();
                newState.splice(idx, 1, state);
                console.log("newState", newState);
                setSquaresState(newState);
            };
            return (
                <Square
                key={idx}
                state={item}
                onClick={() => {
                    onSquareClick(item, onUpdate);
                }}
                />
            );
        });
    }, [squares, setSquaresState, onSquareClick]);


    return <div>{field}</div>;
}


export const Square = ({state, onClick}: {state: SquareState, onClick: () => void}) => {

    return (
        <div className={"game-field__square"} onClick={onClick}>
            hey
            {state === SquareFilling.Cross && <div>крестик</div>}
            {state === SquareFilling.Zero && <div>нолик</div>}
        </div>
    );
}
